           a.count_6_months,
           a.building_count_6_months,
           a.highway_length_6_months,
           a.osm_users,
           a.residential,
           a.gdp,
           a.min_ts,
           a.max_ts,
           a.avgmax_ts,
           a.local_hours,
           a.total_hours,
           a.view_count,
           a.wildfires,
           a.covid19_vaccines,
           a.covid19_confirmed,
           a.population_prev,
