           a.osm_id_lvl_2,
           a.total_road_length,
           a.foursquare_places_count,
           a.foursquare_visits_count,
           a.view_count_bf2402,
           a.raw_mhe_pop_scaled,
           a.raw_mhe_cap_scaled,
           a.raw_mhe_index,
           a.relative_mhe_pop_scaled,
           a.relative_mhe_cap_scaled,
           a.relative_mhe_index,
           a.mhe_index,
           a.life_expectancy_scale,
           a.infant_mortality_scale,
           a.maternal_mortality_scale,
           a.prevalence_undernourished_scale,
           a.vulnerable_health_status_index,
           a.pop_wout_improved_sanitation_scale,
           a.pop_wout_improved_water_scale,
           a.clean_water_access_vulnerability_index,
           a.adult_illiteracy_scale,
           a.gross_enrollment_scale,
           a.years_of_schooling_scale,
           a.pop_wout_internet_scale,
           a.info_access_vulnerability_index,
           a.export_minus_import_percent_scale,
           a.average_inflation_scale,
           a.economic_dependency_scale,
